import { TIMER_COUNT } from '../actions/timerCountAction';

const INITIAL_STATE = {
  timerCount: 30,
};

const timerReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case TIMER_COUNT:
      return {
        ...state,
        timerCount: state.timerCount - 1,
      };
    default:
      return state;
  }
};

export default timerReducer;
