import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { getApiToken } from '../../actions/apiTokenAction';

class LoginContainer extends React.Component {
  static renderSettings() {
    return (
      <div>
        <Link to=" ">
          Settings
        </Link>
      </div>
    );
  }

  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  renderLogin() {
    return (
      <div>
        <label htmlFor="email">E-mail do Gravatar:</label>
        <input
          plasceholder="Email Gravatar"
          type="email"
          data-testid="input-player-name"
          onChange={(e) => this.handleChange(e)}
          name="email"
        />
        <label htmlFor="name">Nome do Jogador:</label>
        <input
          placeholder="Nome"
          onChange={(e) => this.handleChange(e)}
          name="name"
          type="text"
          data-testid="input-gravatar-email"
        />
      </div>
    );
  }

  startGame() {

  }

  renderJogar() {
    const { username, email } = this.state;
    let disabled = false;
    if (username === '' || email === '') {
      disabled = true;
    }
    return (
      <div>
        <Link to=" ">
          <button
            type="button"
            data-testid="btn-play"
            disabled={disabled}
            onClick={this.startGame}
          >
            JOGAR!
          </button>
        </Link>
      </div>
    );
  }

  render() {
    return (
      <div>
        {this.renderLogin()}
        {this.renderJogar()}
        {LoginContainer.renderSettings()}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.gravatarReducer.email,
  token: state.gravatarReducer.token,
  apiToken: state.apiTokenReducer.token,
});

const mapDispatchToProps = (dispatch) => ({
  apiTokenDispatch: () => dispatch(getApiToken()),
});

export default connect(mapStateToProps, mapDispatchToProps)(LoginContainer);
