import { NEXT_QUESTION } from '../actions/newQuestionAction';
import { CHECKED_ANSWER } from '../actions/checkAnswerAction';
import { TIMER_COUNT } from '../actions/timerCountAction';
import { TIME_OUT } from '../actions/timeOutAction';
import { CHOSEN } from '../actions/answerChoosedAction';

const INITIAL_STATE = {
  index: 0,
  correctAnswerClass: '',
  wrongAnswerClass: '',
  disabledBtn: false,
  timerCount: 5,
  points: 0,
  difficulty: 1,
};

const questionsDataReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case NEXT_QUESTION:
      return {
        ...state,
        index: state.index + 1,
        correctAnswerClass: '',
        wrongAnswerClass: '',
        disabledBtn: false,
      };
    case CHECKED_ANSWER:
      return {
        ...state,
        correctAnswerClass: 'correct-answer',
        wrongAnswerClass: 'wrong-answer',
        disabledBtn: true,
        points: state.points + action.points,
      };
    case TIMER_COUNT:
      return {
        ...state,
        timerCount: state.timerCount - 1,
      };
    case TIME_OUT:
      return {
        ...state,
        disabledBtn: true,
        points: state.points + 0,
      };
    default:
      return state;
  }
};

export default questionsDataReducer;
