import {
  API_REQUEST,
  API_RECEIVE_SUCCESS, 
  API_RECEIVE_FAILURE,
} from '../actions/apiTokenAction';

const INITIAL_STATE = {
  token: {},
  loading: false,
  errorMessage: '',
};

const apiTokenReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case API_REQUEST:
      return {
        ...state,
        loading: true,
      };
    case API_RECEIVE_SUCCESS:
      return {
        ...state,
        token: { ...action.token },
        loading: false,
      };
    case API_RECEIVE_FAILURE:
      return {
        ...state,
        errorMessage: action.errorMessage,
        loading: false,
      };
    default:
      return state;
  }
};

export default apiTokenReducer;
