import apiCategoryRequest from '../service/apiCategoryRequest';

export const API_REQUEST = 'API_REQUEST';
export const API_RECEIVE_SUCCESS = 'API_RECEIVE_SUCCESS';
export const API_RECEIVE_FAILURE = 'API_RECEIVE_FAILURE';

const apiRequestAction = () => ({
  type: API_REQUEST,
});

const apiRequestSuccess = (categoriesArr) => ({
  type: API_RECEIVE_SUCCESS,
  categoriesArr,
});

const apiRequestFailure = (error) => ({
  type: API_RECEIVE_FAILURE,
  error,
});

export function getCategoriesRequest() {
  return (dispatch) => {
    dispatch(apiRequestAction());

    return apiCategoryRequest()
      .then(
        (categories) => dispatch(apiRequestSuccess(categories)),
        (error) => dispatch(apiRequestFailure(error.message)),
      );
  };
}
