import React from 'react';
import './QuestionsInfos.css';
import questions from '../dataTest';
import ShuffledButtons from './ShuffledButtons';

class QuestionsInfos extends React.Component {
  constructor (props) {
    super(props);
    this.state = {
      questionIndex: 0,
      disabledBtn: false,
      correctAnswerClass: '',
      wrongAnswerClass: '',
      buttonsArray: [],
    }
    this.nextQuestion = this.nextQuestion.bind(this);
  }

  nextQuestion() {
    this.setState((state) => ({ questionIndex: state.questionIndex + 1}))
  }

  checkAnswer = (stringToTest) => {
    const { question } = this.props;
    this.setState({
      correctAnswerClass: 'correct-answer',
      wrongAnswerClass: 'wrong-answer',
      disabledBtn: true,
    });
    if (stringToTest === question.correct_answer) return console.log(stringToTest)
    return console.log('wrooong')
  }

  arrWithAllButtons = (objQuestion) => {
    const { incorrect_answers, correct_answer } = objQuestion;
    const { disabledBtn, correctAnswerClass, wrongAnswerClass } = this.state;

    const incorrectAnswersArr = incorrect_answers.map((e, index) => 
      (<button
        className={wrongAnswerClass}
        disabled={disabledBtn}
        onClick={() => this.checkAnswer(e)}
        data-testid={`wrong-answer-${index}`}>
          {e}
      </button>));

    const allAnswers = [
      <button
        className={correctAnswerClass}
        disabled={disabledBtn}
        onClick={() => this.checkAnswer(objQuestion.correct_answer)}
        data-testid="correct-answer">
          {correct_answer}
      </button>,
      ...incorrectAnswersArr,
    ];
    return allAnswers;
  }

  shuffleAnswers = (array) => {
    let currentIndex = array.length;
    let temporaryValue = [];
    let randomIndex;

    while (currentIndex !== 0) {
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }
    this.setState({
      answersArray: array,
     })
    return array;
  }

  componentDidMount() {
    const { results } = questions;
    const { questionIndex } = this.state;
    const question = results[questionIndex];
    this.setState({ buttonsArray: this.shuffleAnswers(this.arrWithAllButtons(question)) })
  }

  render() {
    const { results } = questions;
    const { questionIndex, buttonsArray } = this.state;
    const question = results[questionIndex];
    return (
      <section>
        <div className="questions-container">
          <div className="questions-header">
            <span data-testid="question-category">{question.category}</span>
            <p data-testid="question-text">{question.question}</p>
          </div>
          <div className="answers-buttons">
            <ShuffledButtons buttonsArray={buttonsArray} />
          </div>
        </div>
        <button type="button" onClick={this.nextQuestion} >Pr√≥xima</button>
      </section>
    )
  }
}

export default QuestionsInfos;
