import {
  API_REQUEST,
  API_RECEIVE_SUCCESS,
  API_RECEIVE_FAILURE,
} from '../actions/apiQuestionsAction';

const INITIAL_STATE = {
  questions: {
    results: [],
  },
  loading: false,
  errorMessage: '',
};

const apiQuestionsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case API_REQUEST:
      return {
        ...state,
        loading: true,
      };
    case API_RECEIVE_SUCCESS:
      return {
        ...state,
        questions: action.questions,
        loading: false,
      };
    case API_RECEIVE_FAILURE:
      return {
        ...state,
        errorMessage: action.errorMessage,
        loading: false,
      };
    default:
      return state;
  }
};

export default apiQuestionsReducer;
